INSERT INTO `procedure_` (`name`, `reason`, `result`, `tool_id`, `permit_id`, `procedure_type_id`, `cost`, `term`, `method`, `decision`, `deny`, `abuse`) VALUES
('Видача висновку державної екологічної експертизи', '', 'Висновок державної екологічної експертизи.', 3, 4, 1, 'безоплатно', '1. до 45 календарних днів, з продовженням у разі потреби до 60 днів, а у виняткових випадках, залежно від складності проблеми, -до 120 днів; 2. за доопрацьованими матеріалами відповідно до висновків попередньої екологічної експертизи - до 30 календарних днів.', 'Особисто за довіреністю або поштою', '', 'Подання заявником документів з порушенням вимог Порядку передачі документації на державну екологічну експертизу, затвердженого постановою Кабінету Міністрів України від 31.10.1995 № 870 та невідповідність природоохоронному законодавству (стаття 28 Закону України «Про екологічну експертизу», ДБН А.2.2-1-2003 «Склад і зміст матеріалів оцінки впливів на навколишнє середовище (ОВНС) при проектуванні і будівництві підприємств, будинків і споруд»).', 'В судовому порядку'),
('Оформлення та видача погодження (висновку) на заміну номерного агрегату транспортного засобу, а також на переобладнання транспортного засобу без заміни номерних агрегатів', '', 'Видача висновку', 19, 175, 1, '98 гривень 48 копійок', '10 робочих днів з дати одержання заяви про видачу погодження (висновку)', 'Отримання погодження (висновку) або листа з обґрунтуванням причин відмови в його видачі.', '', 'Невідповідність документів установленим вимогам (як-то: неповний перелік, неправильне заповнення тощо); наявність у наданих документах відомостей, що не відповідають дійсності; невідповідність конструкції та технічного стану переобладнаних транспортних засобів вимогам безпеки дорожнього руху, стандартам, правилам і нормам, які діють на території України; встановлення факту знаходження транспортного засобу (складових частин, що мають ідентифікаційні номери) в розшуку; відсутні платіжні документи (квитанції) про сплату вартості за надання послуги.', 'згідно із законодавством'),
('Видача висновку про погодження документації із землеустрою', '', 'Висновок про погодження документації із землеустрою або відмова у її погодженні.', 60, 176, 1, 'безоплатно', 'Протягом 10 робочих днів з дня одержання документації із землеустрою.', 'Особисто або надсилається рекомендованим листом з повідомленням', '', 'Невідповідність її положень вимогам законів та прийнятих відповідно до них нормативно-правових актів, документації із землеустрою або містобудівній документації.',  ''),
('Надання висновку щодо меж географічного місця, з яким пов’язані особливі властивості, певні якості та інші характеристики товару', '', 'Висновок щодо визначення меж географічного місця, з яким пов’язані особливі властивості, певні якості або інші характеристики товару', 27, 177, 1, 'Безоплатно', 'Не може перевищувати 30 календарних днів з дня подання суб’єктом звернення заяви та документів, необхідних для отримання послуги.', 'Особисто заявником (уповноваженою особою заявника), направлення поштою на адресу, вказану у заяві.', '', '',  ''),
('Видача висновку щодо можливості дублювання (нанесення) ідентифікаційних номерів транспортних засобів', '', 'Можливі способи отримання відповіді (результату) – видача висновку щодо можливості дублювання (нанесення) ідентифікаційних номерів транспортних засобів або надсилання листа з обґрунтуванням причини відмови в його видачі.', 19, 178, 1, 'Дублювання ідентифікаційних номерів транспортних засобів - 94 грн.; нанесення ідентифікаційних номерів транспортних засобів - 86 грн.', 'Видача висновку щодо можливості дублювання (нанесення) ідентифікаційних номерів транспортних засобів.', 'Ненадання до сервісного центру МВС повного пакету документів; невірно наведена інформація в документах.', '', 'Згідно діючого законодавства України.',  ''),
('Видача висновку щодо підтвердження відповідності умов виробництва лікарського засобу вимогам належної виробничої практики', '', 'Видача висновку щодо підтвердження відповідності виробництва вимогам належної виробничої практики або прийняття вмотивованого рішення про відмову у його видачі. Повідомлення щодо неможливості видачі Висновку надсилається (видається) заявникові в письмовій формі.', 40, 179, 1, 'безоплатно', 'Строк проведення первинної експертизи поданих документів, що проводиться Держлікслужбою України з метою перевірки їх комплектності, відповідності форми таких документів законодавству, становить не більше 5 робочих днів після реєстрації Заяви на видачу Висновку, крім випадків: 1) для лікарських засобів, призначених для лікування туберкульозу, ВІЛ/СНІД, вірусних гепатитів, онкологічних та рідкісних (орфанних) захворювань, які зареєстровані як лікарський засіб компетентним органом Сполучених Штатів Америки, Швейцарії, Японії, Австралії, Канади або Європейського Союзу, - не більше ніж 2 робочих дні після реєстрації Заяви на видачу Висновку, у разі звернення Заявника (представника Заявника); 2) для лікарських засобів, що зареєстровані за централізованою процедурою Європейським агентством з медичних продуктів (EMA), та для лікарських засобів, що пройшли процедуру прекваліфікації ВООЗ та включені до переліку ВООЗ прекваліфікованих лікарських засобів, - не більше ніж 2 робочих дні після реєстрації Заяви на видачу Висновку, у разі звернення Заявника (представника Заявника). Спеціалізована експертиза, що проводиться Держлікслужбою України з метою складання вмотивованого висновку щодо підтвердження відповідності виробництва лікарських засобів вимогам GMP, проводиться у такі строки: - за умови надання офіційного документа щодо відповідності умов виробництва лікарських засобів вимогам GMP, виданого уповноваженим органом країни-члена PIC/S, - не більше ніж 10 робочих днів після проведення первинної експертизи, крім випадків: 1) для лікарських засобів, призначених для лікування туберкульозу, ВІЛ/СНІД, вірусних гепатитів, онкологічних та рідкісних (орфанних) захворювань, які зареєстровані як лікарський засіб компетентним органом Сполучених Штатів Америки, Швейцарії, Японії, Австралії, Канади або Європейського Союзу, - не більше ніж 3 робочих дні після реєстрації Заяви на видачу Висновку, у разі звернення Заявника (представника Заявника); 2) для лікарських засобів, що зареєстровані за централізованою процедурою Європейським агентством з медичних продуктів (EMA), та для лікарських засобів, що пройшли процедуру прекваліфікації ВООЗ та включені до переліку ВООЗ прекваліфікованих лікарських засобів, - не більше ніж 3 робочих дні після реєстрації Заяви на видачу Висновку, у разі звернення Заявника (представника Заявника). За необхідності під час проведення експертизи Держлікслужбою України може бути направлений відповідний запит до компетентного органу країни-члена PIC/S для з’ясування питань, що виникли під час проведення експертизи, про що повідомляється Заявника (представника Заявника) протягом 10 робочих днів від дати направлення запиту. Час, потрібний для отримання відповіді на запит, не включається до строків проведення спеціалізованої експертизи. У разі некомплектності документів, виявленої під час проведення первинної експертизи, та/або невідповідностей, виявлених під час проведення спеціалізованої експертизи, вимогам Порядку проведення підтвердження відповідності умов виробництва лікарських засобів вимогам належної виробничої практики, затвердженого наказом Міністерства охорони здоров’я України від 27.12.2012 № 1130, зареєстрованого в Міністерстві юстиції України 21 січня 2013 р. за № 133/22665 (далі – Порядок) доданих до Заяви на видачу Висновку документів Заявнику (представнику Заявника) надається про це письмове повідомлення. Час, необхідний для усунення Заявником (представником Заявника) виявлених порушень та/або для надання необхідних документів, не включається до строків проведення первинної або спеціалізованої експертизи. Якщо Заявник (представник Заявника) протягом 30 робочих днів після отримання
INSERT INTO `procedure_` (`name`, `reason`, `result`, `tool_id`, `permit_id`, `procedure_type_id`, `cost`, `term`, `method`, `decision`, `deny`, `abuse`) VALUES
('Надання дозволу на відкриття та експлуатацію вільної митної зони комерційного або сервісного типу', '', 'Витяг з реєстру вільних митних зон комерційного або сервісного типу', 50, 187, 1, 'безоплатно', '23 робочих дні', 'Поштою (рекомендованим листом), а за зверненням заявника - безпосередньо у митниці', '', '1. Ненадання заявником відомостей, передбачених формою заяви, наведеною у додатку до Порядку відкриття та експлуатації вільної митної зони комерційного або сервісного типу, затвердженого наказом Міністерства фінансів України від 30.05.2012 № 633. 2. Надання заявником недостовірних відомостей у заяві та доданих до неї документах, засвідчених копіях документів. 3. Неподання заявником документів, засвідчених копій документів, передбачених пунктом 2.2 розділу ІІ Порядку відкриття та експлуатації вільної митної зони комерційного або сервісного типу, затвердженого наказом Міністерства фінансів України від 30.05.2012 № 633. 4. Ненадання фізичною особою - підприємцем відповідно до Закону України "Про захист персональних даних" згоди на обробку та поширення митницею, яка розглядає заяву, та ДФС України його персональних даних. 5. Невідповідність території або складу вимогам, встановленим Порядком відкриття та експлуатації вільної митної зони комерційного або сервісного типу, затвердженого наказом Міністерства фінансів України від 30.05.2012 № 633',  ''),
('Анулювання дозволу на відкриття та експлуатацію вільної митної зони комерційного або сервісного типу за заявою підприємства', '', 'Наказ митниці щодо анулювання дозволу на відкриття та експлуатацію вільної митної зони комерційного або сервісного типу', 50, 187, 3, 'безоплатно', '10 робочих дныв', 'Поштою (рекомендованим листом), а за зверненням заявника - безпосередньо у митниці', '', 'Відсутні',  ''),
('Зупинення дії дозволу на відкриття та експлуатацію вільної митної зони комерційного або сервісного типу за заявою підприємства', '', 'Наказ митниці щодо зупинення дії дозволу на відкриття та експлуатацію вільної митної зони комерційного або сервісного типу', 50, 187, 4, 'безоплатно', '10 робочих дныв', 'Поштою (рекомендованим листом), а за зверненням заявника - безпосередньо у митниці', '', 'Відсутні',  ''),
('Надання дозволу на відкриття та експлуатацію магазину безмитної торгівлі', '', 'Витяг з реєстру магазинів безмитної торгівлі', 50, 189, 1, 'безоплатно', '20 робочих днів', 'Безпосередньо у Державній фіскальній службі України', '', '1. Приміщення магазину безмитної торгівлі не відповідає вимогам статей 420 та 421 Митного кодексу України. 2. Відсутність документів, необхідних для отримання адміністративної послуги, або наявність у цих документах неправдивих відомостей. 3. Розташування магазину безмитної торгівлі в пункті пропуску через державний кордон України ускладнюватиме забезпечення режиму, установленого у цьому пункті пропуску відповідно до законодавства',  ''),
('Анулювання дозволу на відкриття та експлуатацію магазину безмитної торгівлі за заявою підприємства', '', 'Наказ про анулювання дозволу на відкриття та експлуатацію магазину безмитної торгівлі', 50, 189, 3, 'безоплатно', '20 робочих днів', 'Безпосередньо у Державній фіскальній службі України або поштою', '', 'Заява про анулювання дозволу залишається без розгляду, якщо подана (підписана) особою, яка не має на це повноважень',  ''),
('Зупинення дії дозволу на відкриття та експлуатацію магазину безмитної торгівлі за заявою підприємства', '', 'Наказ про зупинення дії дозволу на відкриття та експлуатацію магазину безмитної торгівлі', 50, 189, 4, 'безоплатно', '20 робочих днів', 'Безпосередньо у Державній фіскальній службі України або поштою', '', 'Заява про зупинення дії дозволу залишається без розгляду, якщо подана (підписана) особою, яка не має на це повноважень',  ''),
('Оформлення та видача дозволу на здійснення експорту/імпорту товарів', '', 'Дозвіл', 66, 191, 1, 'генеральний дозвіл: 150 неоподатковуваних мінімумів доходів громадян (2550 грн.) генеральний дозвіл на здійснення експорту/імпорту товарів на безоплатній основі (у тому числі гарантійне обслуговування або ремонт і постачання запасних частин за рекламаційними актами): 10 неоподатковуваних мінімумів доходів громадян (170 грн.) відкритий дозвіл: 250 неоподатковуваних мінімумів доходів громадян (4250 грн.) відкритий дозвіл на здійснення експорту/імпорту товарів на безоплатній основі (у тому числі гарантійне обслуговування або ремонт і постачання запасних частин за рекламаційними актами): 20 неоподатковуваних мінімумів доходів громадян (340 грн.) разовий дозвіл на здійснення експорту/імпорту товарів на безоплатній основі: 5 неоподатковуваних мінімумів доходів громадян (85 грн.) разовий дозвіл на здійснення експорту/імпорту товарів за контрактом з вартістю: - до 50 тис. гривень включно - 20 неоподатковуваних мінімумів доходів громадян (340 грн.) - від 50 тис. до 500 тис. гривень включно - 30 неоподатковуваних мінімумів доходів громадян (510 грн.) - від 500 тис. до 5000 тис. гривень включно - 60 неоподатковуваних мінімумів доходів громадян (1020 грн.) - від 5000 тис. до 50000 тис. гривень включно - 100 неоподатковуваних мінімумів доходів громадян (1700 грн.) - понад 50000 тис. гривень - 200 неоподатковуваних мінімумів доходів громадян (3400 грн.)', 'Строк розгляду заяви, якщо надання не потребує додаткового міжвідомчого узгодження, встановлюється з дня надходження всіх необхідних документів: - для міжнародних передач товарів військового призначення: до 30 днів - щодо експорту (реекспорту) таких товарів; до 10 днів - щодо імпорту таких товарів; - для міжнародних передач товарів подвійного використання: до 25 днів - щодо експорту (реекспорту) таких товарів; до 10 днів - щодо імпорту таких товарів; - для міжнародних передач товарів, які ввозяться в Україну з наданням державних гарантій: до 10 днів - щодо імпорту таких товарів', 'Послідовність дій: подання документів, необхідних для отримання відповідного дозволу на здійснення міжнародних передач товарів; внесення плати за видачу дозволу; отримання відповідного дозволу на здійснення міжнародних передач товарів', '', 'Не допускається експорт окремих категорій товарів до держав, стосовно яких Радою Безпеки ООН встановлено ембарго на їх експорт, а також у разі, коли за результатами експертизи в галузі державного експортного контролю є підстави вважати, що вони призначені для: створення зброї масового знищення чи засобів її доставки; використання у терористичних або інших протиправних цілях; використання в діяльності, пов’язаній із створенням ядерних вибухових пристроїв, або в діяльності, пов’язаній з ядерним паливним циклом, яка не перебуває під гарантіями МАГАТЕ; використання в діяльності, пов’язаній з придбанням, створенням, накопиченням або застосуванням хімічної зброї як засобу ведення війни; використання в діяльності, пов’язаній з придбанням, створенням, накопиченням або застосуванням збудників захворювань (патогенів) і токсинів як бактеріологічної (біологічної) та токсинної зброї чи її складових частин. Заява суб’єкта здійснення міжнародних передач товарів про надання дозволу на право експорту, реекспорту, імпорту товарів залишається без розгляду, якщо: вона подана (підписана) особою, яка не має на це повноважень; документи подано не в повному обсязі або оформлено з порушенням вимог чинного законодавства', 'Скарга щодо недотримання стандарту може адресуватися Голові Державної служби експортного контролю України або його заступникові і розглядається в терміни згідно з чинним законодавством. Скарга подається поштовим переказом до Держекспортконтролю на адресу: 04080, м. Київ, вул. Фрунзе, 19-21, або на особистому прийомі Голови Держекспортконтролю чи його заступника. Реєстрація скарг проводиться згідно із Законом України „Про звернення громадян”'),
('Видача дозволу на імпорт та експорт зразків видів дикої фауни і флори, сертифікатів на пересувні виставки, реекспорт та інтродукцію з моря зазначених зразків, які є об’єктами регулювання Конвенції про міжнародну торгівлю видами дикої фауни і флори, що перебувають під загрозою зникнення (за винятком осетрових видів риб і виробленої з них продукції)', '', 'Оригінал та дві копії дозволу на імпорт та експорт зразків видів дикої фауни і флори, сертифікатів на пересувні виставки, реекспорт та інтродукцію з моря зазначених зразків, які є об’єктами регулювання Конвенції про міжнародну торгівлю видами дикої фауни і флори, що перебувають під загрозою зникнення в частині осетрових видів риб і виробленої з них продукції.', 66, 192, 1, 'безоплатно', 'Протягом 30 днів з дня подання заяви (у разі необхідності отримання висновку наукового органу строк розгляду поданих документів продовжується на 30 днів)', 'Особисто, представнику за довіреністю або поштою', '', 'Подання заявником документів з порушенням вимог Порядку видачі дозволів на імпорт та експорт зразків видів дикої фауни і флори, сертифікатів на пересувні виставки, реекспорт та інтродукцію з моря зазначених зразків, які є об’єктами регулювання Конвенції про міжнародну торгівлю видами дикої фауни і флори, що перебувають під загрозою зникнення, затвердженого постановою Кабінету Міністрів України від 25 липня 2007 року № 953', 'В судовому порядку'),
('Видача дозволу на імпорт та експорт зразків видів дикої фауни і флори, сертифікатів на пересувні виставки, реекспорт та інтродукцію з моря зазначених зразків, які є об’єктами регулювання Конвенції про міжнародну торгівлю видами дикої фауни і флори, що перебувають під загрозою зникнення, в частині осетрових видів риб і виробленої з них продукції', '', '1. Видача дозволу на імпорт та експорт зразків видів дикої фауни і флори, сертифікатів на пересувні виставки, реекспорт та інтродукцію з моря зазначених зразків, які є об’єктами регулювання Конвенції про міжнародну торгівлю видами дикої фауни і флори, що перебувають під загрозою зникнення (за винятком осетрових видів риб і виробленої з них продукції). 2. Відмова у видачі дозволу на імпорт та експорт зразків видів дикої фауни і флори, сертифікатів на пересувні виставки, реекспорт та інтродукцію з моря зазначених зразків, які є об’єктами регулювання Конвенції про міжнародну торгівлю видами дикої фауни і флори, що перебувають під загрозою зникнення (за винятком осетрових видів риб і виробленої з них продукції). Дозволи або сертифікати для ввезення (імпорту) в Україну зразків CITES видаються лише на зразки видів, занесених до Додатку І CITES.', 66, 77, 1, 'безоплатно', 'Протягом 30 днів з дня їх надходження. У разі необхідності отримання висновку наукового органу строк розгляду поданих документів продовжується на 30 днів. У разі подання заявником документів з порушенням вимог Порядку видачі дозволів на імпорт та експорт зразків видів дикої фауни і флори, сертифікатів на пересувні виставки, реекспорт та інтродукцію з моря зазначених зразків, які є об’єктами регулювання Конвенції про міжнародну торгівлю видами дикої фауни і флори, що перебувають під загрозою зникнення, затвердженого постановою Кабінету Міністрів України від 25 липня 2007 року № 953, його заява про видачу дозволу або сертифіката залишається без розгляду, про що він повідомляється у тижневий строк з дня реєстрації відповідної заяви.', 'Особисто за довіреністю або поштою', '', 'Подання заявником документів з порушенням вимог Порядку видачі дозволів на імпорт та експорт зразків видів дикої фауни і флори, сертифікатів на пересувні виставки, реекспорт та інтродукцію з моря зазначених зразків, які є об’єктами регулювання Конвенції про міжнародну торгівлю видами дикої фауни і флори, що перебувають під загрозою зникнення, затвердженого постановою Кабінету Міністрів України від 25 липня 2007 року № 953.', 'В судовому порядку'),
('Видача дозволу на імпорт товарів', '', 'Дозвіл на імпорт товарів.', 66, 194, 1, 'безоплатно', 'Розгляд заяви та прийняття рішення про видачу дозволу або відмову в його видачі приймається протягом двох робочих днів.', 'Видача заявнику дозволу на імпорт здійснюється лише за наявності оформленого доручення на його одержання. Дозвіл на імпорт видається в одному примірнику.', '', 'У разі подання заяви не в установленій формі вона повертається заявнику', 'Рішення Міністерства про відмову у видачі дозволу на імпорт може бути оскаржено заявником у встановленому чинним законодавством України порядку.'),
('Видача дозволів на перевезення через митний кордон України вибухових матеріалів для юридичних осіб', '', 'Видача дозволу на перевезення через митний кордон України вибухових матеріалів для юридичних осіб', 66, 195, 1, '270 гривень', 'У тридцятиденний строк з дати одержання заяви про видачу дозволу на перевезення через митний кордон України вибухових матеріалів, на які поширюється дозвільна система.', 'Видача одержувачеві адміністративної послуги дозволу, або надсилання листа з обґрунтуванням причини відмови в його видачі.', '', '1) відсутність повного пакету документів; 2) відсутність платіжного документа (квитанції), який засвідчує оплату адміністративної послуги.', 'Рішення може бути оскаржено у встановленому чинним законодавством України порядку.'),
('Видача дозволів на перевезення через митний кордон України вогнепальної зброї, бойових припасів до неї, основних частин зброї, пневматичної, холодної і охолощеної зброї, а також пристроїв та патронів до них для юридичних осіб та фізичних осіб підприємців', '', 'Видача дозволу на перевезення через митний кордон України вогнепальної зброї, бойових припасів до неї, основних частин зброї, пневматичної, холодної і охолощеної зброї, а також пристроїв та патронів до них для юридичних осіб.', 66, 196, 1, '270 гривень', 'У тридцятиденний строк з дати одержання заяви про видачу дозволу на придбання і перевезення вогнепальної зброї, боєприпасів, інших предметів, на які поширюється дозвільна система.', 'Видача одержувачеві адміністративної послуги дозволу, або надсилання листа з обґрунтуванням причини відмови в його видачі.', '', '1) відсутність повного пакету документів; 2) відсутність платіжного документа (квитанції), який засвідчує оплату адміністративної послуги.', 'Рішення може бути оскаржено у встановленому чинним законодавством України порядку.'),
('Видача дозволу на поїздку територією іноземних держав під час виконання нерегулярних перевезень пасажирів автомобільним транспортом у міжнародному сполученні', '', 'Дозвіл на міжнародні нерегулярні перевезення пасажирів', 66, 197, 1, '105,68 грн.', 'Для отримання дозволу уповноважена особа звертається не пізніше ніж за п’ять робочих днів до здійснення відповідного перевезення пасажирів.', 'Автомобільний перевізник або уповноважена особа.', '', '',  ''),
('Видача дозволу на право ввезення (вивезення) чи транзиту наркотичних засобів, психотропних речовин або прекурсорів наркотичних засобів і психотропних речовин', '', 'Дозвіл', 66, 31, 1, '1% вартості контракту на імпортні операції і 2% вартості контракту на експортні операції. За оформлення і видачу дозволу на експорт сірчаної кислоти та соляної кислоти і здійснення контролю за їх обігом з підприємств – виробників цих кислот справляється плата у розмірі 0,2% вартості відповідного контракту, згідно постанови Кабінету Міністрів України від 03.02.1997 р. № 146', 'До 30 днів згідно постанови Кабінету Міністрів України від 03.02.1997 р. № 146', 'Особисто або представником (за дорученням)', '', 'Подання суб’єктом господарювання неповного пакету документів, необхідних для одержання документа дозвільного характеру, згідно із встановленим вичерпним переліком. Виявлення в документах, поданих суб’єктом господарювання, недостовірних відомостей. Негативний висновок за результатами проведених експертиз та обстежень або наукових і технічних оцінок, необхідних для видачі документа дозвільного характеру.', 'Згідно чинного законодавства України'),
('Видача дозволу на реекспорт товарів іноземного походження', '', 'Дозвіл на реекспорт товарів іноземного походження', 66, 199, 1, 'безоплатно', '15 робочих днів розглядаються подані документи та приймається рішення про видачу дозволу або відмову в його видачі', 'Дозвіл видається в одному примірнику і скріплюється гербовою печаткою Міністерства економічного розвитку і торгівлі України. Дозвіл видається особі, яка має належним чином оформлене доручення на його одержання. Про видані дозволи на реекспорт товарів іноземного походження Міністерство інформує Державну митну службу України.', '', 'Рішення про відмову у видачі дозволу може бути прийнято в разі: подання документів з порушенням вимог , зазначених у розділі 2 Положення про порядок оформлення та видачі Міністерством економіки та з питань європейської інтеграції України дозволів на реекспорт товарів іноземного походження та товарів українського походження, затвердженого наказом Міністерства економіки та з питань європейської інтеграції України від 10.02.2004 № 52; виявлення в поданих документах недостовірної інформації; застосування демпінгових цін або інших елементів недобросовісної торгової практики, що завдають шкоди економічним інтересам України; наявності обмежень з боку третіх країн щодо ввезення на їх митну територію відповідних товарів; застосування до однієї із сторін зовнішньоекономічного договору (контракту) на момент звернення спеціальної санкції - тимчасового зупинення зовнішньоекономічної діяльності відповідно до статті 37 Закону України "Про зовнішньоекономічну діяльність"; невідповідності платіжних умов зовнішньоекономічного договору (контракту) вимогам чинного законодавства України. У разі відмови у видачі дозволу заявнику надсилається лист з обґрунтуванням причин відмови.', 'Суб’єкт господарювання має право повторно звернутися до Міністерства економічного розвитку і торгівлі України з проханням про видачу дозволу, усунувши причини відмови.'),
('Видача дозволу на реекспорт товарів українського походження', '', 'Дозвіл на реекспорт товарів українського походження.', 66, 200, 1, 'Для одержання дозволу на реекспорт товарів українського походження іноземному суб’єкту господарської діяльності необхідно подати копію платіжного доручення (з відміткою банку про сплату) про перерахування до Державного бюджету України суми відшкодування вивізного (експортного) мита згідно із пунктом 1.4 Положення про порядок оформлення та видачі Міністерством економіки та з питань європейської інтеграції України дозволів на реекспорт товарів іноземного походження та товарів українського походження, затвердженого наказом Міністерства економіки та з питань європейської інтеграції України від 10.02.2004 № 52', '15 робочих днів розглядаються подані документи та приймається рішення про видачу дозволу або відмову в його видачі.', 'Дозвіл видається в одному примірнику і скріплюється гербовою печаткою Міністерства. Дозвіл видається особі, яка має належним чином оформлене доручення на його одержання. Про видані дозволу на реекспорт товарів українського походження Міністерство інформує уповноважені органи іноземної країни, з митної території якої здійснюється реекспорт цих товарів.', '', 'Рішення про відмову у видачі дозволу може бути прийнято в разі: подання документів з порушенням вимог , зазначених у розділі 2 Положення про порядок оформлення та видачі Міністерством економіки та з питань європейської інтеграції України дозволів на реекспорт товарів іноземного походження та товарів українського походження, затвердженого наказом Міністерства економіки та з питань європейської інтеграції України від 10.02.2004 № 52; виявлення в поданих документах недостовірної інформації; застосування демпінгових цін або інших елементів недобросовісної торгової практики, що завдають шкоди економічним інтересам України; наявності обмежень з боку третіх країн щодо ввезення на їх митну територію відповідних товарів; застосування до однієї із сторін зовнішньоекономічного договору (контракту) на момент звернення спеціальної санкції - тимчасового зупинення зовнішньоекономічної діяльності відповідно до статті 37 Закону України "Про зовнішньоекономічну діяльність"; У разі відмови у видачі дозволу заявнику надсилається лист з обґрунтуванням причин відмови.', 'Суб’єкт господарювання має право повторно звернутися до Міністерства економічного розвитку і торгівлі України з проханням про видачу дозволу, усунувши причини відмови.'),
('Проведення експертного дослідження транспортних засобів і супровідних документів з видачею експертного висновку', '', 'Експертний висновок', 40, 201, 1, 'Для транспортних засобів усіх категорій вітчизняного виробництва та країн СНД або окремих агрегатів становить 152 гривні 63 копійки; для транспортних засобів усіх категорій іноземного виробництва або окремих агрегатів становить 190 гривень 15 копійок; для мототранспорту, причепів вітчизняного виробництва та країн СНД становить 148 гривень 63 копійки; для мототранспорту, причепів іноземного виробництва становить 186 гривень 15 копійки; для мопедів або окремих їх агрегатів становить 54 гривні 83 копійки.', 'Відразу з дати одержання заяви про видачу свідоцтва про реєстрацію або його дубліката та номерних знаків, облікової картки та номерних знаків для разових поїздок.', 'Видача одержувачеві свідоцтва про реєстрацію або його дубліката та номерних знаків, облікової картки та номерних знаків для разових поїздок або надсилання листа з обґрунтуванням причини відмови в його видачі.', '', 'Ненадання до сервісного центру МВС повного пакету документів; невірно наведена інформація в документах.', 'Згідно діючого законодавства України.'),
('Надання згоди (дозволу) на відчуження державного майна', '', 'Розпорядчий акт (наказ) про надання згоди на відчуження державного майна або про відмова в наданні такої згоди - у формі листа.', 26, 202, 1, 'Безоплатно', '30 календарних днів з дати надходження документів у повному обсязі', 'Надсилання рішення Фонду на адресу одержувача адміністративної послуги', '', '1) майно, запропоноване до відчуження, не відповідає вимогам, установленим абзацами третім - шостим пункту 6 Порядку; 2) суб’єкт господарювання подав не в повному обсязі документи, передбачені Порядком, або не подав їх у встановлений цим Порядком строк, а також за наявності у документах суперечностей; 3) техніко-економічне обґрунтування доцільності відчуження майна не містить необхідних економічних та/або технічних розрахунків, що підтверджують необхідність відчуження майна (зокрема визначення впливу відчуження майна на цілісність майнового комплексу); 4) за результатами рецензування висновок про вартість майна не може бути погоджений; 5) суб’єктом оціночної діяльності та/або оцінювачами порушено вимоги законодавства під час оцінки майна; 6) відповідно до вимог, установлених пунктом 8 Порядку, пропозиції щодо організатора аукціону з продажу нерухомого майна не можуть бути погодженими', 'відповідно до чинного законодавства'),
('Реєстрація зовнішньоекономічних контрактів (договорів) на здійснення експортних операцій з брухтом чорних металів, брухтом легованих чорних металів та брухтом кольорових металів', '', 'Картка реєстрації-обліку зовнішньоекономічного контракту (договору).', 66, 203, 2, 'безоплатно', 'Протягом 10 робочих днів з дати подання документів проводиться їх реєстрація, оформлення та видача карток реєстрації-обліку зовнішньоекономічного контракту (договору) або відмова у їх реєстрації', 'Картка реєстрації-обліку зовнішньоекономічного контракту (договору) та оригінал зовнішньоекономічного контракту (договору) видаються заявникові або інший уповноваженій особі', '', '• подання не всіх документів; • невідповідность умов зовнішньоекономічного контракту (договору) законодавству у сфері зовнішньоекономічної діяльності та кон’юнктурі світового ринку; • застосування до суб’єкта зовнішньоекономічної діяльності України або до його іноземного контрагента спеціальних санкцій згідно із статтею 37 Закону України "Про зовнішньоекономічну діяльність", що діють на момент звернення щодо реєстрації зовнішньоекономічного контракту (договору); • заборона з боку іноземних держав на ввезення на їх митну територію відходів та брухту чорних металів, брухту легованих чорних металів та брухту кольорових металів походженням з України; • існування доказів порушення суб’єктом зовнішньоекономічної діяльності України або його іноземним контрагентом законодавства в сфері зовнішньоекономічної діяльності; • невідповідность даних інформаційної картки зовнішньоекономічного контракту (договору) даним такого контракту (договору).', 'Заявник має право доопрацювати документи згідно з вимогами. У цьому разі розгляд документів продовжується на термін до внесення заявником доопрацьованих документів. Заявник має право оскаржити відмову в реєстрації зовнішньоекономічного контракту (договору) в порядку, встановленому законодавством.'),
('Прийняття рішення про анулювання кваліфікаційного сертифіката інженера-землевпорядника або інженера-геодезиста за заявою суб’єкта звернення', '', 'Прийняття рішення про анулювання кваліфікаційного сертифіката інженера-землевпорядника або інженера-геодезиста.', 55, 204, 3, 'Безоплатно', 'Не може перевищувати 30 календарних днів з дня подання суб’єктом звернення заяви', 'Відправляється поштою на адресу, вказану у зверненні.', '', '',  ''),
('Прийняття рішення про видачу кваліфікаційного сертифіката сертифікованого інженера-землевпорядника', '', 'Сертифікат інженера-землевпорядника або відмову у його отриманні.', 55, 205, 1, 'Безоплатно', 'Не може перевищувати 30 календарних днів з дня подання суб’єктом звернення заяви та документів, необхідних для отримання послуги.', 'Видається заявнику (довіреній особі) в письмовій формі під розписку у журналі', '', 'Отримання кваліфікаційного сертифіката інженера-землевпорядника у порядку, визначеному пунктом 3 Прикінцевих і перехідних положень Закону України від 02.10.2012 №5394-VI «Про внесення змін до деяких законодавчих актів України щодо дерегуляції господарської діяльності з проведення робіт із землеустрою та землеоціночних робіт» раніше.',  '');
INSERT INTO `procedure_` (`name`, `reason`, `result`, `tool_id`, `permit_id`, `procedure_type_id`, `cost`, `term`, `method`, `decision`, `deny`, `abuse`) VALUES
('Видача Індивідуальної ліцензії на здійснення резидентами майнових інвестицій за межами України', '', 'Індивідуальна ліцензія на здійснення резидентами майнових інвестицій за межами України.', 66, 206, 1, '384,16 грн. за видачу індивідуальної ліцензії', 'Протягом 15 робочих днів розглядає подані документи та приймає рішення про видачу індивідуальної ліцензії або відмову в її видачі', 'Документи подаються поштою. Подання документів в електронному вигляді та активація кнопки “Замовити послугу” буде дозволено при внесенні відповідних змін до нормативно-правових актів. Заявник отримує індивідуальну ліцензію за формою, встановленою наказом Мінекономіки від 07.11.2000 № 240 “Про порядок прийняття та розгляду Міністерством економічного розвитку і торгівлі України документів для отримання індивідуальних ліцензій на здійснення резидентами майнових інвестицій за межами України” . Ліцензія видається фізичній особі - суб’єкту підприємницької діяльності, керівнику суб’єкта зовнішньоекономічної діяльності за наявності документа, що посвідчує їх особу, або вповноваженому представникові суб’єкта зовнішньоекономічної діяльності за умови подання до Міністерства економічного розвитку і торгівлі України або уповноваженого органу оригіналу довіреності, що засвідчує право на отримання ліцензії, та документа, що посвідчує особу представника.', '', 'Відсутність усіх необхідних документів; невідповідність поданих документів вимогам законодавства України; виявлення в поданих документах недостовірної інформації; порушення проти заявника справи про банкрутство; перебування майна заявника в податковій заставі у зв’язку із заборгованістю із сплати податків та внесення інших загальнодержавних платежів; заборона або обмеження щодо здійснення іноземних інвестицій, передбачені законодавством України та/або законодавством країни, де розміщуються інвестиції; застосування до заявника на момент здійснення інвестицій спеціальних санкцій, передбачених статтею 37 Закону України "Про зовнішньоекономічну діяльність”, або наявність фактів порушення установлених термінів проведення розрахунків за зовнішньоекономічними операціями; неперебування заявника на обліку як платника податків.', 'Рішення може бути оскаржено у встановленому чинним законодавством України порядку'),
('Видача ліцензії на імпорт товарів', '', 'Ліцензія на імпорт товарів', 66, 207, 1, 'у режимі неавтоматичного ліцензування - 780 гривень; у режимі автоматичного ліцензування - 220 гривень.', 'У разі запровадження режиму автоматичного ліцензування строк видачі ліцензії не повинен перевищувати 10 робочих днів від дати одержання заявки та інших необхідних документів, що відповідають установленим вимогам. У разі запровадження режиму неавтоматичного ліцензування строк розгляду заявок не повинен перевищувати 30 днів від дати їх одержання, якщо заявки розглядаються в порядку їх надходження, та більше 60 днів від дати закінчення оголошеного строку приймання заявок, якщо всі вони розглядаються одночасно', 'Видача ліцензії здійснюється за умови подання до Мінекономрозвитку оригіналу документа, що підтверджує сплату державного збору за видачу ліцензії в розмірі, установленому Кабінетом Міністрів України. Ліцензія видається вповноваженому представникові суб’єкта зовнішньоекономічної діяльності за умови подання до Мінекономрозвитку довіреності або копії довіреності, засвідченої в установленому законодавством порядку, на її одержання та пред’явлення документа, що посвідчує особу.', '', 'Рішення про відмову у видачі ліцензії суб’єкту зовнішньоекономічної діяльності може бути прийняте в разі: подання документів, зазначених у пункті 2 ПОЛОЖЕННЯ про порядок ліцензування імпорту товарів, з порушенням вимог щодо комплектності; невідповідності поданих документів законодавству України; вичерпання встановленої квоти на відповідні товари (у разі застосування процедури розгляду заявок у порядку їх надходження); застосування до суб’єкта зовнішньоекономічної діяльності або до його іноземного контрагента спеціальної санкції у вигляді тимчасового зупинення зовнішньоекономічної діяльності згідно із статтею 37 Закону України "Про зовнішньоекономічну діяльність" ( 959-12 ); дій суб’єкта зовнішньоекономічної діяльності, що призводять до порушення взятих Україною міжнародних зобов’язань; порушення суб’єктом зовнішньоекономічної діяльності або його іноземним контрагентом законодавства України у сфері зовнішньоекономічної діяльності.', 'Рішення може бути оскаржено у встановленому чинним законодавством України порядку'),
('Видача ліцензій на право імпорту, експорту спирту етилового, коньячного і плодового, спирту етилового ректифікованого виноградного, спирту етилового ректифікованого плодового, алкогольних напоїв та тютюнових виробів', '', 'Видача ліцензії за формою, встановленою наказом Міністерства зовнішніх економічних зв’язків і торгівлі від 14.05.96 № 260 “Про затвердження зразків ліцензій на право здійснення експорту, імпорту, оптової та роздрібної торгівлі спиртом етиловим, коньячним і плодовим, алкогольними напоями та тютюновими виробами" або відмова у видачі ліцензії, оформлена письмово на підставі висновків структурних підрозділів Міністерства економічного розвитку і торгівлі України, якими здійснювався розгляд документів для видачі ліцензій.', 66, 208, 1, '780 гривень на рік', 'Мінекономрозвитку протягом 10 календарних днів розглядає подані документи та приймає рішення про видачу ліцензії або відмову в її видачі.', 'Оформлення та видача ліцензій здійснюються на підставі поданої заявником копії платіжного доручення (з оригінальною відміткою банку) про зарахування до місцевого бюджету річної плати за ліцензію. Видача ліцензії здійснюється за наявності належним чином оформленого доручення на її одержання та документа, що засвідчує особу.', '', 'Невідповідність поданих документів чинному законодавству України; порушення заявником законодавства України у сфері зовнішньоекономічної діяльності.', 'Заявник має право оскаржити відмову у видачі ліцензії в порядку, визначеному чинним законодавством України'),
('Списання виноградників усіх форм власності, які закладені за рахунок державних коштів', '', 'Наказ Міністерства агарної політики та продовольства України, яким надається погодження на списання насаджень.', 19, 209, 1, 'безоплатно', 'не визначений', 'Поштою', '', 'Рішення про відмову в наданні згоди на списання майна приймається у разі, коли: - суб’єктом господарювання не подано повного пакету документів, а також за наявності у зазначених документах суперечностей та неточностей; - у техніко-економічному обґрунтуванні доцільності списання багаторічних насаджень відсутні економічні та/або технічні розрахунки, що підтверджують необхідність списання такого майна; - відповідно до законодавства заборонено розпоряджатися майном.', 'Рішення може бути оскаржено у встановленому чинним законодавством України порядку'),
('Прийняття рішення щодо введення родовища або окремого покладу нафти і газу: в дослідно-промислову розробку', '', 'Наказ Міністерства енергетики та вугільної промисловості', 19, 210, 1, 'безоплатно', 'Місячний термін з дати надходження до Міністерства енергетики та вугільної промисловості', 'Поштою, особисто', '', 'Неповний перелік документів, які додаються до заяви', 'Повторне подання заяви та документів'),
('Прийняття рішення щодо введення родовища або окремого покладу нафти і газу: у промислову розробку', '', 'Наказ Міністерства енергетики та вугільної промисловості', 19, 211, 1, 'безоплатно', 'Місячний термін з дати надходження до Міністерства енергетики та вугільної промисловості', 'Поштою, особисто', '', 'Неповний перелік документів, які додаються до заяви', 'Повторне подання заяви та документів'),
('Декларування оптово-відпускних цін на лікарські засоби та вироби медичного призначення', '', 'Наказом МОЗ України задекларована оптово-відпускна ціна на лікарський засіб або виріб медичного призначення на підставі якого внесено відомості до реєстру оптово-відпускних цін на лікарські засоби і вироби медичного призначення, який розміщено у вільному доступі на офіційному веб-сайті МОЗ України www.moz.gov.ua у розділі «Фармсектор»', 0, 91, 1, 'безоплатно', '10 робочих днів (з моменту реєстрації документів в МОЗ України)', 'Офіційний веб-сайт МОЗ України www.moz.gov.ua у розділі «Фармсектор»/ «Реєстр оптово-відпускних цін на лікарські засоби» та/або «Реєстр оптово-відпускних цін на вироби медичного призначення».', '', 'Якщо документи ,подані не в повному обсязі, оформлені з порушенням установлених вимог, МОЗ повідомляє про це заявникові в письмовій формі протягом п’яти робочих днів з дати їх реєстрації', 'В установленому законодавством порядку'),
('Оформлення та видача погодження (висновку) на заміну номерного агрегату транспортного засобу, а також на переобладнання транспортного засобу без заміни номерних агрегатів', '', 'Видача погодження', 40, 213, 1, '98 гривень 48 копійок', '10 робочих днів з дати одержання заяви про видачу погодження (висновку)', 'Отримання погодження (висновку) або листа з обґрунтуванням причин відмови в його видачі.', '', 'Невідповідність документів установленим вимогам (як-то: неповний перелік, неправильне заповнення тощо); наявність у наданих документах відомостей, що не відповідають дійсності; невідповідність конструкції та технічного стану переобладнаних транспортних засобів вимогам безпеки дорожнього руху, стандартам, правилам і нормам, які діють на території України; встановлення факту знаходження транспортного засобу (складових частин, що мають ідентифікаційні номери) в розшуку; відсутні платіжні документи (квитанції) про сплату вартості за надання послуги.', 'згідно із законодавством'),
('Погодження набуття (збільшення) юридичною чи фізичною особою істотної участі у професійному учаснику фондового ринку (крім банку) (щодо депозитарної та розрахунково-клірингової діяльності) (Фізична особа (іноземець))', '', 'Рішення Комісії про погодження набуття істотної участі або її збільшення у статутному (складеному) капіталі професійного учасника фондового ринку. Рішення Комісії є дійсним протягом шести місяців з дня його видачі.', 80, 214, 1, 'Безоплатно', 'Національна комісія з цінних паперів та фондового ринку в установленому нею порядку приймає відповідне рішення у строк не пізніше ніж протягом місяця з дня подання заяви та відповідних документів. Рішення про погодження набуття або збільшення істотної участі у професійному учаснику фондового ринку або відмову у такому погодженні приймається на засіданні Комісії згідно з регламентом її роботи та оформлюється відповідним рішенням Комісії. Письмове повідомлення про прийняття відповідного рішення (з доданням його копії) надсилається заявнику протягом трьох робочих днів з дати прийняття цього рішення (підписується керівником структурного підрозділу Комісії, що здійснює розгляд заяви та відповідних документів).', 'Письмове повідомлення про прийняття рішення про погодження набуття або збільшення істотної участі у професійному учаснику фондового ринку або відмову у такому погодженні (з доданням копії рішення)', '', 'Відсутні',  ''),
('Погодження набуття (збільшення) юридичною чи фізичною особою істотної участі у професійному учаснику фондового ринку (крім банку) (щодо компаній з управління активами) (Фізичні особи - резиденти)', '', 'Рішення НКЦПФР', 80, 215, 1, 'Безоплатно', '30 календарних днів', 'НКЦПФР направляє письмове повідомлення про прийняття відповідного рішення (з доданням його копії) протягом трьох робочих днів з дати прийняття такого рішення.', '', 'Відсутні',  ''),
('Погодження статуту недержавного пенсійного фонду', '', 'Погодження статуту недержавного пенсійного фонду.', 55, 216, 1, 'Плата законодавством не встановлена', 'Погодження статуту недержавного пенсійного фонду здійснюється Нацкомфінпослуг протягом 30 календарних днів з дати надходження документів, передбачених Положенням.', 'Про прийняття рішення про відмову в погодженні статуту недержавного пенсійного фонду Нацкомфінпослуг протягом 3 робочих днів після прийняття відповідного рішення повідомляє представника недержавного пенсійного фонду в письмовій формі із зазначенням підстав для відмови. Уразі прийняття рішення про погодження статуту пенсійного фонду на примірниках статуту робиться напис (штамп) «Погоджено» із зазначенням реквізитів рішення про погодження. Напис (штамп) скріплюється підписом особи, уповноваженої наказом Голови Нацкомфінпослуг на погодження статуту недержавного пенсійного фонду, та печаткою. Повідомлення про погодження статуту недержавного пенсійного фонду надсилається (надається) представнику недержавного пенсійного фонду протягом 3 робочих днів з дати погодження.', '', '',  ''),
('Оформлення та видача посвідчення про реєстрацію суб’єкта здійснення міжнародних передач товарів', '', 'Посвідчення про реєстрацію', 66, 217, 1, '200 неоподатковуваних мінімумів доходів громадян (3400 грн.)', 'Термін проведення попередньої експертизи не може перевищувати 30 днів з дати подання усіх необхідних документів до Держекспортконтролю, а в разі виникнення потреби у додатковому міжвідомчому узгодженні – після завершення такого узгодження. До строку розгляду звернення не зараховується час, необхідний для одержання від суб’єкта підприємницької діяльності України додаткової інформації', 'Послідовність дій: подання до Держекспортконтролю документів у паперовому вигляді, необхідних для проведення попередньої експертизи товарів; внесення плати за видачу посвідчення про реєстрацію; отримання, за результатами попередньої експертизи, посвідчення про реєстрацію як суб’єкта здійснення міжнародних передач товарів разом з відповідними роз’ясненнями про особливості здійснення таких передач', '', 'У разі коли додаткова інформація, за запитом Держекспортконтролю, від суб’єкта підприємницької діяльності України не надійшла до Держекспортконтролю протягом двох місяців, заява вважається відхиленою і розгляду не підлягає', 'Скарга щодо недотримання стандарту може адресуватися Голові Державної служби експортного контролю України або його заступникові і розглядається в терміни згідно з чинним законодавством. Скарга подається поштовим переказом до Держекспортконтролю на адресу: 04080, м. Київ, вул. Фрунзе, 19-21, або на особистому прийомі Голови Держекспортконтролю чи його заступника. Реєстрація скарг проводиться згідно із Законом України "Про звернення громадян"'),
('Проведення державної експертизи з енергозбереження з видачею експертного висновку енерготехнологічних частини техніко-економічних обґрунтувань і проектів будівництва нових та розширення (реконструкції, технічного переобладнання, модернізації) діючих об’єктів та підприємств з річним споживанням паливно-енергетичних ресурсів 1 тис. і більше тонн у перерахунку на умовне паливо – за рішенням замовника в разі надання преференцій (субсидій, податкових або фінансово-кредитних пільг) за рахунок бюджетних коштів', '', 'Після завершення робіт, пов’язаних з державною експертизою з енергозбереження , замовник отримує експертний висновок. Передана документація повертається замовнику , який надіслав її на розгляд, із супроводжувальним листом і з позначками експерта при потребі доопрацювання документації', 50, 201, 1, '835,93 грн.', 'Експертний висновок', 'Заявнику може бути відмовлено у наданні адміністративної послуги у разі неповноти або недостовірності відомостей, що містяться в наданих на експертизу матеріалах', '', '',  ''),
('Проведення державної експертизи з енергозбереження з видачею експертного висновку проектів інструктивно-методичних та нормативно-технічних актів, будівельних норм і правил, документації на створення та придбання нової енергоємної техніки, технології і матеріалів, інших документів і матеріалів, що регламентують всі види діяльності у сфері енергозбереження', '', 'Експертний висновок', 50, 201, 1, '1155,02 грн.', 'Максимальний строк не повинен перевищувати 30 днів', 'Замовник отримує експертний висновок. Передана документація повертається замовнику, який надіслав її на розгляд, із супроводжувальним листом із позначками експерта при потребі доопрацювання документації', '', 'Заявнику може бути відмовлено у наданні адміністративної послуги у разі неповноти або недостовірності відомостей, що містяться в наданих на експертизу матеріалах',  ''),
('Проведення державної експертизи з енергозбереження з видачею експертного висновку проектів схем розвитку і розміщення продуктивних сил, проектів розвитку галузей народного господарства, територіальних схем енергозабезпечення, іншої передпланової та передпроектної документації', '', 'Експертний висновок', 50, 201, 1, '895,88 грн.', 'Максимальний строк надання адміністративної енергозбереження не повинен перевищувати 30 днів', 'Після завершення робіт, пов’язаних з державною експертизою з енергозбереження, замовник отримує експертний висновок. Передана документація повертається замовнику , який надіслав її на розгляд, із супроводжувальним листом і з позначками експерта при потребі доопрацювання документації', '', 'Заявнику може бути відмовлено у наданні адміністративної послуги у разі неповноти або недостовірності відомостей, що містяться в наданих на експертизу матеріалах',  ''),
('Оформлення та видача висновку на здійснення тимчасового ввезення/вивезення товарів: разовий висновок', '', 'Висновок', 66, 218, 1, 'Разовий 5 неоподатковуваних мінімумів доходів громадян (85 грн.) ', 'Строк розгляду заяви суб’єкта про надання висновку на право здійснення міжнародної передачі товарів, якщо його надання не потребує додаткового міжвідомчого узгодження, встановлюється з дня надходження всіх необхідних документів: для міжнародних передач товарів військового призначення: до 30 днів - щодо тимчасового вивезення (ввезення) таких товарів; до 10 днів - щодо транзиту таких товарів, або тимчасового вивезення та тимчасового ввезення таких товарів у випадках, передбачених пунктами 18, 19, 24 та 25 Порядку здійснення державного контролю за міжнародними передачами товарів військового призначення, затвердженого постановою Кабінету Міністрів України від 20.11.2003 № 1807 (із змінами); для міжнародних передач товарів подвійного використання: до 25 днів - щодо тимчасового вивезення (ввезення) таких товарів; до 10 днів - щодо транзиту таких товарів, або тимчасового вивезення та тимчасового ввезення таких товарів у випадках, передбачених пунктами 19, 20, 26 та 27 Порядку здійснення державного контролю за міжнародними передачами товарів подвійного використання, затвердженого постановою Кабінету Міністрів України від 28.01.2004 № 86 (із змінами). До строку розгляду заяви не зараховується час, необхідний для отримання від суб’єкта додаткових відомостей або документів. Якщо додаткові відомості або документи не надійшли протягом двох місяців, заява вважається відхиленою і розгляду не підлягає. У разі потреби в додатковому міжвідомчому узгодженні (перевірка кінцевого споживача чи посередника, отримання інформації про можливе відхилення від заявленого кінцевого використання товарів, проведення консультацій з відповідними органами держав - учасниць міжнародних режимів експортного контролю) міністерства, інші центральні органи виконавчої влади, Служба зовнішньої розвідки України протягом 15 днів після отримання відповідного запиту, надають висновок про можливість здійснення міжнародної передачі. Якщо надання висновку потребує терміну, що перевищує 15 днів, міністерства, інші центральні органи виконавчої влади, Служба зовнішньої розвідки України письмово повідомляють про це Держекспортконтроль із зазначенням підстав для продовження терміну та визначають додатковий термін для надання висновку, який не може перевищувати 15 днів. У такому разі загальний термін розгляду заяв з урахуванням міжвідомчого узгодження не повинен перевищувати 90 днів', 'Послідовність дій: подання до Держекспортконтролю документів, необхідних для отримання відповідного висновку на здійснення міжнародних передач товарів; внесення плати за видачу висновку на здійснення міжнародних передач товарів; отримання відповідного висновку на здійснення міжнародних передач товарів', '', 'Заява суб’єкта здійснення міжнародних передач товарів про надання висновку на здійснення міжнародної передачі товарів залишається без розгляду, якщо: вона подана (підписана) особою, яка не має на це повноважень; документи подано не в повному обсязі або оформлено з порушенням вимог чинного законодавства', 'Скарга щодо недотримання стандарту може адресуватися Голові Державної служби експортного контролю України або його заступникові і розглядається в терміни згідно з чинним законодавством. Скарга подається поштовим переказом до Держекспортконтролю на адресу: 04080, м. Київ, вул. Фрунзе, 19-21, або на особистому прийомі Голови Держекспортконтролю чи його заступника. Реєстрація скарг проводиться згідно із Законом України „Про звернення громадян”'),
('Оформлення та видача висновку на проведення переговорів: разовий висновок', '', 'Висновок', 66, 219, 1, 'Разовий: 20 неоподатковуваних мінімумів доходів громадян (340 грн.) ', 'Розгляд заяв здійснюється Держекспортконтролем протягом не більш як 15 днів, якщо вирішення цього питання не потребує додаткового міжвідомчого узгодження. У разі коли для прийняття рішення необхідно узгодити з іншими органами виконавчої влади чи залучити до розгляду Міжвідомчу комісію з політики військово-технічного співробітництва та експортного контролю, термін прийняття остаточного рішення не повинен перевищувати 10 днів після такого узгодження чи отримання відповідного рішення зазначеної Комісії. До терміну розгляду заяв не зараховується час, необхідний для отримання додаткової інформації. У разі потреби в додатковому міжвідомчому узгодженні (перевірка кінцевого споживача чи посередника, отримання інформації про можливе відхилення від заявленого кінцевого використання товарів, проведення консультацій з відповідними органами держав - учасниць міжнародних режимів експортного контролю) органи виконавчої влади, Служба зовнішньої розвідки України протягом 15 днів після отримання відповідного запиту надають висновок про можливість проведення переговорів. Якщо надання висновку потребує терміну, що перевищує 15 днів, органи виконавчої влади письмово повідомляють про це Держекспортконтроль із зазначенням підстав для продовження терміну та визначають додатковий термін для надання висновку, який не може перевищувати 15 днів. У такому разі загальний термін розгляду заяв з урахуванням міжвідомчого узгодження не повинен перевищувати 90 днів', 'Послідовність дій: подання до Держекспортконтролю документів, необхідних для отримання висновків щодо можливості проведення переговорів; внесення плати за видачу висновку; отримання висновків Держекспортконтролю щодо можливості проведення переговорів', '', 'Не допускається проведення переговорів щодо: міжнародних передач ядерної, хімічної і біологічної зброї, а також матеріалів, обладнання та технологій, спеціально призначених для її створення; експорту окремих категорій товарів військового призначення та подвійного використання до держав, щодо яких встановлено повне ембарго на поставки таких товарів. Заява суб’єкта про надання висновку залишається без розгляду, якщо: вона подана (підписана) особою, яка не має на це повноважень; документи подано не в повному обсязі або оформлено з порушенням вимог чинного законодавства', 'Скарга щодо недотримання стандарту може адресуватися Голові Державної служби експортного контролю України або його заступникові і розглядається в терміни згідно з чинним законодавством. Скарга подається поштовим переказом до Держекспортконтролю на адресу: 04080, м. Київ, вул. Фрунзе, 19-21, або на особистому прийомі Голови Держекспортконтролю чи його заступника. Реєстрація скарг проводиться згідно із Законом України „Про звернення громадян”'),
('Державна реєстрація (перереєстрація) проектів (програм) міжнародної технічної допомоги', '', 'Оформлення та видача реєстраційної картки проекту (програми) міжнародної технічної допомоги; надсилання листа-відповіді про призупинення чи відмову у державній реєстрації', 50, 220, 2, 'безоплатно', 'Мінекономрозвитку розглядає подані усіма заявниками проекту (програми) документи протягом 10 робочих днів починаючи від дня надходження документів і приймає рішення щодо проведення державної реєстрації проекту (програми). У разі коли документи для державної реєстрації подано не в повному обсязі, Мінекономрозвитку зупиняє розгляд клопотання про державну реєстрацію і письмово повідомляє про це заявника протягом п’яти робочих днів.', 'Реєстраційна картка проекту (програми) міжнародної технічної допомоги видається заявникові або надсилається поштою на його адресу, або лист-відповідь про призупинення чи відмову у державній реєстрації надсилається поштою на адресу заявника', '', '1) невідповідність поданих заявником документів вимогам пункту 14 Порядку залучення, використання та моніторингу міжнародної технічної допомоги, затвердженого постановою Кабінету Міністрів України від 15.02.2002 № 153; 2) невідповідність проекту (програми) узгодженій з донором стратегічній або щорічній програмі; 3) виявлення в поданих документах недостовірної інформації', 'Рішення може бути оскаржено у встановленому чинним законодавством України порядку'),
('Реєстрація розрахункових книжок', '', 'Зареєстрована розрахункова книжка', 36, 221, 2, 'безоплатно', 'Не пізніше двох робочих днів', 'Безпосередньо в Державних податкових інспекціях', '', 'Відсутня реєстрація РРО в контролюючому органі (у разі реєстрації книги ОРО на РРО). Документи подано не в повному обсязі. В контролюючому органі наявні відомості про анулювання ліцензії на право здійснення відповідної господарської діяльності. В контролюючому органі наявні відомості про скасування реєстрації РРО (у разі реєстрації книги ОРО на РРО). Суб’єкт господарювання не перебуває на обліку в контролюючому органі, до якого подано документи. Припинено або скасовано документ на право власності або інший документ, що дає право на розміщення господарської одиниці, де має використовуватись розхункова книжка. Суб’єкта господарювання не включено до Єдиного державного реєстру юридичних осіб та фізичних осіб - підприємців. Суб’єкт господарювання або його відокремлені підрозділи не перебувають на обліку в контролюючому органі за адресою господарської одиниці, де буде використовуватися книга ОРО (розрахункова книжка)',  '');
INSERT INTO `procedure_` (`name`, `reason`, `result`, `tool_id`, `permit_id`, `procedure_type_id`, `cost`, `term`, `method`, `decision`, `deny`, `abuse`) VALUES
('Реєстрація Правил (змін до Правил) Центрального депозитарію цінних паперів', '', 'Рішення Комісії про реєстрацію Правил Центрального депозитарію та надання статусу Центрального депозитарію або про відмову у реєстрації Правил Центрального депозитарію та наданні статусу Центрального депозитарію', 16, 222, 2, 'Безоплатно', 'Національна комісія з цінних паперів та фондового ринку в установленому нею порядку приймає відповідне рішення протягом 30 календарних днів з дня одержання відповідних документів. (За наслідками розгляду заяви та поданих документів Комісією протягом 15 робочих днів приймається рішення про реєстрацію змін до Правил ЦД або про відмову в такій реєстрації).', 'Письмове повідомлення про прийняття рішення про реєстрацію Правил ЦД та надання статусу Центрального депозитарію або про відмову у реєстрації Правил ЦД та наданні статусу Центрального депозитарію (з доданням копії рішення)', '', 'Підставами для відмови в реєстрації внутрішніх документів особи, яка провадить клірингову діяльність, і змін до них є: 1) невідповідність документів, поданих на реєстрацію, вимогам законодавства, у тому числі нормативно-правовим актам Національної комісії з цінних паперів та фондового ринку; 2) подання документів не в повному обсязі, наявність у них неповної або недостовірної інформації; 3) невідповідність розміру статутного капіталу вимогам закону.',  ''),
('Допуск цінних паперів іноземних емітентів до обігу на території України', '', 'Рішення НКЦПФР про допуск цінних паперів іноземних емітентів до обігу на території України.', 3, 223, 1, 'Безоплатно', 'Не пізніше 30 календарних днів з дати надходження документів.', 'Протягом трьох робочих днів після прийняття рішення про допуск або про відмову в допуску копія такого рішення надається (надсилається) суб’єкту звернення.', '', 'Відсутні',  ''),
('Дострокове припинення виконання (відмови від виконання) делегованих повноважень саморегулівній організації професійних учасників фондового ринку', '', 'Рішення НКЦПФР', 16, 224, 1, 'безоплатно', 'Розгляд документів, поданих для державної реєстрації фізичної особи – підприємця, які містяться в Єдиного державного реєстру юридичних осіб, фізичних осіб - підприємців та громадських формувань - протягом 24 годин після надходження документів, поданих для державної реєстрації та проведення інших реєстраційних дій, крім вихідних та святкових днів.', 'Особисто/через уповноважену особу/ поштою/ через портал електронних сервісів юридичних осіб, фізичних осіб - підприємців та громадських формувань за посиланням: https://kap.minjust.gov.ua/', '', 'Підстави для відмови у державній реєстрації фізичної особи - підприємця: 1) документи подано особою, яка не має на це повноважень; 2) у Єдиному державному реєстрі містяться відомості про судове рішення щодо заборони у проведенні реєстраційної дії; 3) не усунуто підстави для зупинення розгляду документів протягом встановленого строку; 4) наявні обмеження на зайняття підприємницькою діяльністю, встановлені законом; 5) наявність в Єдиному державному реєстрі запису, що фізична особа вже зареєстрована як фізична особа - підприємець.', 'Оскарженя до Міністерства юстиції України та його територіальних органів або до суду.'),
('Погодження набуття (збільшення) юридичною чи фізичною особою істотної участі у професійному учаснику фондового ринку (для торговців цінними паперами та фондових бірж) (Фізична особа - нерезидент)', '', 'Рішення НКЦПФР', 80, 225, 1, 'Безоплатно', 'Не пізніше тридцяти днів з дня одержання документів', 'Комісія надсилає письмове повідомлення про прийняття відповідного рішення (з доданням його копії) протягом трьох робочих днів з дати прийняття цього рішення. Таке повідомлення підписується керівником структурного підрозділу Комісії, що здійснює розгляд заяви та відповідних документів.', '', 'Відсутні',  ''),
('Погодження набуття (збільшення) юридичною чи фізичною особою істотної участі у професійному учаснику фондового ринку (для торговців цінними паперами та фондових бірж) (Фізична особа - резидент)', '', 'Рішення НКЦПФР', 80, 226, 1, 'Безоплатно', 'Не пізніше тридцяти днів з дня одержання документів', 'Комісія надсилає письмове повідомлення про прийняття відповідного рішення (з доданням його копії) протягом трьох робочих днів з дати прийняття цього рішення. Таке повідомлення підписується керівником структурного підрозділу Комісії, що здійснює розгляд заяви та відповідних документів.', '', 'Відсутні',  ''),
