INSERT INTO `procedure_` (`name`, `reason`, `result`, `tool_id`, `permit_id`, `procedure_type_id`, `cost`, `term`, `method`, `decision`, `deny`, `abuse`) VALUES
('Погодження статуту (змін до статуту) фондової біржі', '', 'Погоджений статут (зміни до нього)', 3, 560, 1, 'Безоплатно', 'Не пізніше тридцяти днів з дня одержання документів', 'Повідомлення про прийняте Комісією рішення в письмовій формі та копія відповідного рішення надсилаються заявнику не пізніше тридцяти днів з дня одержання заяви та документів на реєстрацію змін до правил або погодження статуту (змін до статуту) фондової біржі.', '', 'Відсутні',  ''),
('Погодження статуту (змін до статуту) Центрального депозитарію цінних паперів', '', 'Отримання засвідчених НКЦПФР примірників статуту (змін до статуту) здійснюється заявником особисто або через уповноважену особу.', 3, 561, 1, 'Безоплатно', 'Протягом місяця з дня надходження документів, передбачених пунктом 4, до НКЦПФР.', '', '', '',  ''),
('Погодження на створення ліцензіатом відокремленого підрозділу за межами України (діяльності з організації торгівлі на фондовому ринку, діяльності з торгівлі цінними паперами, діяльності з управління іпотечним покриттям (для банків))', '', 'Рішення НКЦПФР', 3, 562, 1, 'Безоплатно', 'Не пізніше тридцяти календарних днів з дня одержання документів', 'Комісія надсилає письмове повідомлення про прийняття відповідного рішення (з доданням його копії). Таке повідомлення підписується керівником структурного підрозділу Комісії, що здійснює розгляд заяви та відповідних документів', '', 'Відсутні',  ''),
('Надання погодження на створення професійним учасником фондового ринку відокремленого підрозділу за межами України (щодо депозитарної діяльності (депозитарна діяльність депозитарної установи, діяльність із зберігання активів інститутів спільного інвестування, діяльність із зберігання активів пенсійних фондів), клірингової діяльності)', '', 'Рішення Комісії', 3, 563, 1, 'Безоплатно', 'Комісія приймає рішення про надання погодження ліцензіату на створення відокремленого підрозділу за межами України не пізніше ніж у місячний строк з дня подання всіх необхідних документів або відмову в такому погодженні. Погодження, яке надано Комісією на створення відокремленого підрозділу ліцензіата за межами України, є дійсним протягом шести місяців з дня його надання', 'Письмове повідомлення про прийняття рішення органом ліцензування про прийняте рішення, надсилаючи відповідного листа, з доданням копії цього рішення та обґрунтуванням причин у разі відмови у наданні погодження на створення', '', 'Відсутні',  ''),
('Визначення можливості застосування конкретного типу радіоелектронного засобу або випромінювального пристрою на території України', '', 'Рішення про визначення можливості або неможливості застосування заявленого типу РЕЗ або ВП на території України', 3, 564, 1, 'безоплатно', 'Граничний строк до 73 календарних днів.', 'Повідомлення про прийняте рішення надсилається Заявнику протягом трьох робочих днів після його прийняття на вказану в Заяві адресу електронної пошти. На бажання Заявника повідомлення додатково надсилається в письмовій формі на вказану в Заяві поштову адресу в межах України.', '', 'Заява залишається без розгляду, якщо: 1) документи подані з порушенням вимог частини четвертої цієї статті; 2) радіотехнологія, для якої заявлено радіоелектронний засіб або випромінювальний пристрій, не визначена у Плані використання радіочастотного ресурсу України; 3) термін застосування радіотехнології, для якої заявлено радіоелектронний засіб або випромінювальний пристрій, згідно з Планом використання радіочастотного ресурсу України закінчується менше ніж за рік', 'В судовому порядку'),
('Віднесення харчових продуктів до категорії харчових продуктів спеціального дієтичного споживання, функціональних харчових продуктів та дієтичних добавок та їх державної реєстрації', '', 'Інформування про прийняття рішення про віднесення харчових продуктів до категорії спеціальних та їх внесення до Реєстру або про відмову у її реєстрації з обґрунтуванням причин відмови.', 19, 565, 1, 'безоплатно', 'Надання адміністративної послуги з віднесення харчових продуктів до категорії харчових продуктів спеціального дієтичного споживання, функціональних харчових продуктів та дієтичних добавок здійснюється протягом 30 робочих днів після надходження документів до експертної установи Державна санітарно-епідеміологічна експертиза та клінічне випробування нового виду спеціального харчового продукту проводяться протягом 90 робочих днів після надходження документів Державна реєстрація здійснюється протягом трьох робочих днів з моменту отримання від відповідної експертної установи висновку державної санітарно-епідеміологічної експертизи та звіту щодо віднесення харчових продуктів до категорії харчових продуктів спеціального дієтичного споживання, функціональних харчових продуктів та дієтичних добавок', 'Рішення про віднесення харчових продуктів до категорії спеціальних та їх внесення до Реєстру або відмова у її реєстрації з обґрунтуванням причин відмови', '', 'Неналежне оформлення або ненадання вичерпного переліку документів Невідповідність харчового продукту обов’язковим параметрам безпечності та мінімальним специфікаціям якості', 'Згідно чинного законодавства'),
('Державна реєстрація косметичних засобів, які містять генетично модифіковані організми або отримані з їх використанням', '', 'Отримання рішення про внесення продукції до Реєстру', 19, 566, 2, 'безоплатно', '120 днів', 'Інформування про прийняття рішення про внесення продукції до Реєстру або про відмову у її реєстрації з обґрунтуванням причин відмови', '', '1.Неналежне оформлення або ненадання вичерпного переліку документів, визначених п. 9 зазначеної Інформаційної картки 2. Негативні висновки державної екологічної та/або санітарно-епідеміологічної експертизи косметичних засобів, які містять генетично модифіковані організми або отримані з їх використанням 3. Надходження науково обґрунтованої інформації щодо небезпеки продукції для здоров’я людини або навколишнього природного середовища у разі використання її за цільовим призначенням', 'Відповідно до вимог чинного законодавства'),
('Внесення установи (організації) до Реєстру неприбуткових організацій та установ', '', 'Рішення про внесення, повторне внесення, виключення, відмову у внесенні установи (організації) до/з Реєстру неприбуткових установ та організацій або присвоєння установі (організації) іншої ознаки неприбутковості', 70, 567, 2, 'безоплатно', '30 календарних днів з дня подання заяви', 'Видається уповноваженому представнику заявника або відправляється поштою (з повідомленням про вручення)', '', 'Надання документів не в повному обсязі та/або надання документів з недостовірними даними.',  ''),
('Перереєстрація установи (організації) у Реєстрі неприбуткових організацій та установ', '', 'Рішення про внесення, повторне внесення, виключення, відмову у внесенні установи (організації) до/з Реєстру неприбуткових установ та організацій або присвоєння установі (організації) іншої ознаки неприбутковості', 57, 568, 16, 'безоплатно', '30 календарних днів з дня подання заяви', 'Видається уповноваженому представнику заявника або відправляється поштою (з повідомленням про вручення)', '', 'Надання документів не в повному обсязі та/або надання документів з недостовірними даними',  ''),
('Прийняття рішення про допуск перевізників-резидентів до перевезень на умовах Конвенції МДП', '', 'Рішення про допуск перевізників-резидентів до перевезень на умовах Конвенції МДП', 3, 569, 1, 'безоплатно', 'Протягом двох тижнів після складання іспиту стосовно допущення перевізника до перевезень на умовах Конвенції МДП', 'Безпосередньо у Державній фіскальній службі України або поштою', '', 'Невідповідність критеріям, визначеним частиною другою додатка 9 Конвенції МДП',  ''),
('Реєстрація договору, який стосується права автора на твір', '', 'Рішення про реєстрацію договору, який стосуються прав автора на твір', 18, 570, 1, '4,25 неоподатковуваних мінімумів доходів громадян — для фізичних осіб 11,5 неоподатковуваних мінімумів доходів громадян — для юридичних осіб', 'Розгляд заявки і прийняття рішення про реєстрацію договору, який стосується права автора на твір, здійснюється протягом місяця від дати надходження до Державної служби інтелектуальної власності України правильно оформлених документів заявки. У випадку надсилання заявнику запитів про надання додаткових документів, потрібних для прийняття відповідного рішення у разі виникнення сумніву щодо вірогідності інформації, що містять подані документи, відповідне рішення приймається в двотижневий термін від дати надходження додаткових документів, але не пізніше двох місяців від дати одержання заявником запиту.', 'Рішення видається заявнику безпосередньо або надсилається на адресу, зазначену в заяві.', '', 'Підставою для прийняття рішення про відмову в реєстрації договору, який стосується права автора на твір, є невідповідність поданих документів встановленим вимогам', 'Скарга щодо недотримання Стандарту може подаватися на адресу Державної служби інтелектуальної власності України і розглядається в терміни згідно з чинним законодавством. Скарга подається засобами поштового зв’язку на адресу: вул. Василя Липківського, 45, м. Київ-35, 03680 або на особистому прийомі керівника чи його заступника. Реєстрація скарг проводиться згідно з Законом України «Про звернення громадян». Захист прав може здійснюватися у судовому порядку відповідно до законодавства.'),
('Виключення аудиторської фірми або аудитора (за заявою) з реєстру аудиторів, які можуть проводити аудиторські перевірки фінансових установ', '', 'Виключення аудиторської фірми або аудитора з Реєстру', 105, 571, 1, 'Плата законодавством не встановлена', 'Не більше 30 календарних днів з дня подання суб’єктом звернення заяви та документів.', 'Копія розпорядження Нацкомфінпослуг про виключення аудиторської фірми або аудитора з Реєстру має бути надіслана рекомендованим листом або вручена особисто керівнику аудиторської фірми або аудитору протягом 15 робочих днів з дати прийняття цього розпорядження.', '', '',  ''),
('Визнання інституту спільного інвестування таким, що відповідає / не відповідає вимогам щодо мінімального обсягу активів інституту спільного інвестування', '', 'Розпорядження НКЦПФР.', 105, 572, 1, 'Безоплатно', '15 робочих днів з дня надходження до НКЦПФР заяви та відповідного пакету документів.', 'Отримання Розпорядження НКЦПФР здійснюється керівником заявника особисто або через уповноважену особу за адресою: 01010, м. Київ, вул. Московська, 8, корпус 30, 10 поверх, кабінет 1002.', '', 'Відсутні',  ''),
('Виключення відомостей про інститути спільного інвестування з Єдиного державного реєстру інститутів спільного інвестування (для виключення відомостей про корпоративний інвестиційний фонд)', '', 'Розпорядження НКЦПФР', 105, 573, 14, 'Безоплатно', '15 робочих днів з дати надходження до НКЦПФР заяви та відповідного пакету документів', 'Отримання Розпорядження НКЦПФР здійснюється заявником особисто або через уповноважену особу', '', 'Відсутні',  ''),
('Виключення відомостей про інститути спільного інвестування з Єдиного державного реєстру інститутів спільного інвестування (для виключення відомостей про пайовий інвестиційний фонд)', '', 'Розпорядження НКЦПФР', 107, 574, 14, 'Безоплатно', '15 робочих днів з дати надходження до НКЦПФР заяви та відповідного пакету документів', 'Отримання Розпорядження НКЦПФР здійснюється заявником особисто або через уповноважену особу', '', 'Відсутні',  ''),
('Відновлення обігу цінних паперів (крім цінних паперів інститутів спільного інвестування) (Для відновлення обігу акцій у зв’язку із скасуванням рішення про ліквідацію акціонерного товариства)', '', 'Національна комісія з цінних паперів та фондового ринку видає розпорядження про відновлення обігу цінних паперів, засвідчене підписом уповноваженої особи та печаткою Комісії.', 19, 575, 1, 'Безоплатно', 'Реєструвальний орган протягом 15 робочих днів з дати подання заяви та всіх необхідних документів для відновлення обігу цінних паперів видає розпорядження про відновлення обігу цінних паперів.', 'Отримання розпорядження про відновлення обігу цінних паперів здійснюється заявником особисто або через уповноважену особу', '', 'Відсутні',  ''),
